import React from 'react';

import SiteNavbar from './SiteNavbar';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import Samarth from '../img/team/samring.png';
import Vicky from '../img/team/vickyring.png';
import Aditya from '../img/team/aditring.png';
import Justin from '../img/team/justinring.png';
import Ervin from '../img/team/ervinring.png';
import Spider from '../img/team/spider.png';

import '../css/Team.css';

const Team = () => {
	return (
		<div className="team">
			<SiteNavbar />
			<Container fluid>
				<Row>
					<Col xs={6}>
						<h2>Vicky Li</h2>
						<h3>Co-President and VP Marketing and Design</h3>
						<p>
							Hi everyone, I am a second-year at UC Berkeley studying Computer
							Science with a minor in Data Science. I recently got into Web
							Development after attending a hackathon, and I believe that it is
							an extremely valuable topic that is not extensively covered in
							Berkeleyâ€™s current CS curriculum. I am also very passionate about
							UI/UX Design, and I am excited to teach others these skills as
							well. Outside of staring at screens, I am an avid photographer,
							videographer, and dancer. I enjoy making Youtube videos in my free
							time, so as they say, like and subscribe, and go bears!
						</p>
					</Col>
					<Col xs={2}>
						<img src={Samarth} alt="samarth" />
						<img src={Spider} alt="spider" />
					</Col>
					<Col xs={2}>
						<img src={Vicky} alt="vicky" />
						<img src={Ervin} alt="ervin" />
					</Col>
					<Col xs={2}>
						<img src={Aditya} alt="aditya" />
						<img src={Justin} alt="justin" />
					</Col>
				</Row>
			</Container>
		</div>
	);
};

export default Team;
