import React from 'react';

import SiteNavbar from './SiteNavbar';

import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import TeamText from '../img/team/team-text.png';
import Samarth from '../img/team/samring.png';
import Vicky from '../img/team/vickyring.png';
import Aditya from '../img/team/aditring.png';
import Justin from '../img/team/justinring.png';
import Ervin from '../img/team/ervinring.png';
import Spider from '../img/team/spider.png';

import '../css/Team.css';

const Team = () => {
	const bios = {
		vicky: {
			name: 'Vicky Li',
			role: 'Co-President and VP Marketing and Design',
			description:
				'Hi everyone, I am a second-year at UC Berkeley studying Computer Science with a minor in Data Science.I recently got into Web Development after attending a, and I believe that it is an extremely valuable topic that is not extensively covered in Berkeley’s current CS curriculum. I am also very passionate about UI/UX Design, and I am excited to teach others these skills as well.Outside of staring at screens, I am an avid photographer, videographer, and dancer. I enjoy making Youtube videos in my free time, so as they say, like and subscribe, and go bears!',
		},
		samarth: {
			name: 'Samarth Goel',
			role: 'Co-President and VP Finance',
			description: 'placeholder',
		},
	};
	return (
		<div className="team">
			<SiteNavbar />
			<div className="title-container">
				<img src={TeamText} alt="team text" />
				<p className="subtitle">
					Hover over photo to
					<br />
					view bios
				</p>
			</div>
			<Container fluid className="body">
				<Row>
					<Col xs={6} className="text-col">
						<h2 className="name" id="name">
							{bios.vicky.name}
						</h2>
						<h3 className="role">Co-President and VP Marketing and Design</h3>
						<p className="description">{bios.vicky.description}</p>
					</Col>
					<Col xs={2} className="image-col">
						<img src={Samarth} alt="samarth" className="team-image" />
						<img src={Spider} alt="spider" className="team-image" />
					</Col>
					<Col xs={2} className="image-col">
						<img src={Vicky} alt="vicky" className="team-image" />
						<img src={Ervin} alt="ervin" className="team-image" />
					</Col>
					<Col xs={2} className="image-col">
						<img src={Aditya} alt="aditya" className="team-image" />
						<img src={Justin} alt="justin" className="team-image" />
					</Col>
				</Row>
			</Container>
		</div>
	);
};

export default Team;
